;; **
;;; ### Combined
;; **

;; @@
(let [tweet-count (map #(mc/count @db "tweets" {:created_at {$gte % $lt (t/plus % (t/days 1))}}) dates)
        days (range  2 (inc (inc days-running)))]
    (plot/list-plot tweet-count :joined true :plot-size 900))
