    (plot/compose
      (plot/list-plot sz-counts :joined true :color "red" :plot-size 900 :plot-range [[0 30] [0 140]])
      (plot/list-plot faz-counts :joined true :color "green")
      (plot/list-plot bild-counts :joined true :color "blue")))
