(def avg-size-delays
    (->> all-delays
         (map #(map vector (range (count %)) %))
         (apply concat)
         (group-by first)
         (map (fn [[size coll]] [size ((comp float #(/ % 3600) /)  (reduce + (map (fn [[k v]] v) coll)) (count coll))]))))
