(def delay-lm
  (let [delay-sizes (->> all-delays
  	                     (map #(pmap vector (range (count %)) %))
                         (apply concat)
                         (pmap (fn [[k v]] [k (/ v 60)])))]
  (linear-model (pmap first delay-sizes) (pmap second delay-sizes))))
