(letfn [(ts-freq [pubs]
          (->> pubs
           (map (comp t/hour :ts))
           frequencies
           (map (fn [[k v]] [k (/ v (count pubs))]))
           (sort-by first)))]

  (compose
    (list-plot (ts-freq user-publications) :plot-size 800 :joined true :colour "green" :plot-range [:all [0 0.1]])
    (list-plot (ts-freq source-publications) :plot-size 800 :joined true :colour "red")))

