(letfn [(hs-count [pubs]
          (->> pubs
           (map :hashtags)
           flatten
               (remove nil?)
           (into #{})
            count
           ))]

  (bar-chart ["sources" "users"] [(/ (hs-count source-publications) (count source-publications))
                                  (/ (hs-count user-publications) (count user-publications))]))
